#!/bin/bash

. ./hiker.sh

function test_why_quoting_is_important()
{
  local expected="6 * 9"
  local actual="6 * 9"
  # This fails because assertEquals gets more than 2 arguments
  # assertEquals ${expected} ${actual} 
  # This works because assertEquals gets 2 arguments
  assertEquals "${expected}" "${actual}"
}

function test_dollar_double_parentheses_is_an_arithmetic_expression()
{
  assertEquals 42 $((6 * 7))
}

function test_dollar_single_parenthesis_is_command_substitution()
{
  assertEquals "6 * 9" "$(answer)"
}

# load and run shUnit2
. shunit2
