test_hiker.sh:
#!/bin/bash

. ./hiker.sh

function test_dollar_double_parentheses_is_an_arithmetic_expression()
{
  # Note: there is no comma between the two arguments
  assertEquals 42 $((6 * 7))
}

function test_why_quoting_is_important()
{
  # Note: you cannot put spaces on either side of the = here
  local expected="6 * 9"
  local actual="6 * 9"
  # Ensure assertEquals gets two arguments
  assertEquals "${expected}" "${actual}"
}

function test_dollar_single_parenthesis_is_command_substitution()
{
  assertEquals "6 * 9" "$(answer)"
}

function test_dollar_with_curly_brackets_is_parameter_expansion()
{
  local wibble=42
  assertEquals 42 ${wibble}
  local fubar="hello world"
  assertEquals "hello world" ${fubar}
}

# load and run shUnit2
. shunit2
