test_hiker.sh:
#!/bin/bash

. ./hiker.sh

function test_dollar_curly_brackets_is_parameter_expansion()
{
  # Note: you cannot put spaces on either side of the = 
  local wibble=42
  # Note: there is no comma between the two arguments  
  assertEquals 42 ${wibble}
}

function test_importance_of_quoting()
{
  local expected="hello world"
  local actual="hello world"
  # Ensure assertEquals gets two arguments
  assertEquals "${expected}" "${actual}"
}

function test_dollar_double_round_parentheses_is_an_arithmetic_expression()
{
  assertEquals 42 $((6 * 7))
}

function test_double_round_parenthesis_is_arithmetic_instruction()
{
  local x=42
  # without (( )) this would give "x++ command not found"
  ((x++))
  assertEquals 43 ${x}
}

function test_dollar_single_round_parenthesis_is_command_substitution()
{
  # answer() is in hiker.sh
  assertEquals "6 * 9" "$(answer)"
}

function test_double_quotes_interpolate()
{
  local wibble=42
  assertEquals "42" "${wibble}"
}

function test_single_quotes_dont_interpolate()
{
  local wibble=42
  assertEquals '${wibble}' '${wibble}'
}

function test_double_square_brackets_are_conditional_return_0_or_1()
{
  local x="[[ 'x' == 'x' ]]"
  assertEquals 0 ${x}
}

function test_single_round_parenthesis_is_a_subshell()
{
  (cd /)
  assertTrue "${PWD} is under /sandboxes" "[[ $PWD =~ /sandboxes* ]]"
  cd /
  assertFalse "${PWD} is under /sandboxes" "[[ $PWD =~ /sandboxes* ]]"
}

# load and run shUnit2
. shunit2
