test_hiker.sh:
#!/bin/bash

. ./hiker.sh

function test_using_a_pipe_to_get_one_line_of_a_file()
{
  local expected="fail [message]"
  local actual=$(head -18 help.txt | tail -1)
  assertEquals "${expected}" "${actual}"
}

function test_finding_the_largest_file_in_etc()
{
  local expected="sdsd"
  local actual=$(ls -l /etc | sort -k5rn | head -1)
  assertEquals "${expected}" "${actual}"  
}

# load and run shUnit2
. shunit2
