test_piping.sh:
#!/bin/bash

test_using_a_pipe_to_get_line_18_of_help_text_file()
{
  local expected="fail [message]"
  local actual=$(head -18 shunit2_help.txt | tail -1)
  assertEquals "${expected}" "${actual}"
}

test_finding_the_name_of_the_largest_file_in_etc_dir()
{
  local expected="services"
  local actual=$(ls -l /etc | sort -k5rn | head -1 | awk '{print $9}')
  assertEquals "${expected}" "${actual}"  
}

test_append_the_output_of_ls_to_a_file()
{
  echo "Hello" > simple.txt
  ls -al /etc >> simple.txt
  cat simple.txt
}

test_list_first_three_subdirs_of_etc_dir_alphabetically()
{
  local expected=$(printf "%s\n%s\n%s" "apk" "conf.d" "crontabs")
  local actual=$(ls -l /etc | grep ^d | awk '{print $9}' | sort | head -3)
  assertEquals "${expected}" "${actual}"  
}

# load and run shUnit2
source shunit2
