test_io_redirection.sh:
#!/bin/bash

function test_greater_writing_stdout_to_a_file()
{
  assertFalse "[ -e copy.txt ]"
  cat shunit2_help.txt > copy.txt
  assertTrue  "[ -e copy.txt ]"
  local d=$(diff copy.txt shunit2_help.txt)
  assertTrue "[ -z $d ]"
  rm copy.txt
}

function test_greater_greater_appending_stdout_to_a_file()
{
  assertFalse "[ -e copy.txt ]"
  cat shunit2_help.txt > copy.txt
  assertTrue  "[ -e copy.txt ]"
  cat shunit2_help.txt >> copy.txt
  local d=$(diff copy.txt shunit2_help.txt)
  assertFalse "[ -z $d ]"
  rm copy.txt  
}

function test_less_than_reading_stdout_from_a_file()
{
  local e1=$(wc -L shunit2_help.txt)
  local longestLine_e1=$(echo $e1 | awk '{print $1}')
  assertEquals "86" $longestLine_e1

  local e2=$(wc -L < shunit2_help.txt)
  local longestLine_e2=$(echo $e2 | awk '{print $1}')
  assertEquals "86" $longestLine_e2
}

function test_redirecting_stderr_to_a_file()
{
   cat -3 shunit2_help.txt 2> error.log
   echo "~~~~~"
   cat error.log
   echo "~~~~~"
   local topLine=$(head -n 1 error.log)
   echo "~~~~~"
   cat $topLine
   echo "~~~~~"
   echo "DDDDDDD"
}

# load and run shUnit2
source shunit2
