test_io_redirection.sh:
#!/bin/bash

function test_greater_than_to_write_output_to_a_file()
{
  assertFalse "[ -e copy.txt ]"
  cat shunit2_help.txt > copy.txt
  assertTrue  "[ -e copy.txt ]"
  local d=$(diff copy.txt shunit2_help.txt)
  assertTrue "[ -z $d ]"
  rm copy.txt
}

function test_greater_greater_appends_to_a_file()
{
  assertFalse "[ -e copy.txt ]"
  cat shunit2_help.txt > copy.txt
  assertTrue  "[ -e copy.txt ]"
  cat shunit2_help.txt >> copy.txt
  local d=$(diff copy.txt shunit2_help.txt)
  assertFalse "[ -z $d ]"
  rm copy.txt  
}

# load and run shUnit2
source shunit2
