test_fizz_buzz.sh:
#!/bin/bash

. ./fizzbuzz.sh

function test_multiples_of_3_are_Fizz()
{
  assertEquals 'Fizz' $(fizzbuzz 3)
  assertEquals 'Fizz' $(fizzbuzz 6)
}

function test_multiples_of_5_are_Buzz()
{
  assertEquals 'Buzz' $(fizzbuzz  5)
  assertEquals 'Buzz' $(fizzbuzz 10)
}

function test_multiples_of_3_and_5_are_FizzBuzz()
{
  assertEquals 'FizzBuzz' $(fizzbuzz 15)
  assertEquals 'FizzBuzz' $(fizzbuzz 30)
}

function test_multiples_of_neither_3_nor_5_are_themselves()
{
  assertEquals '1' $(fizzbuzz 1)
  assertEquals '2' $(fizzbuzz 2)
}

# load and run shUnit2
. shunit2

fizzbuzz.sh:
#!/bin/bash

function fizzbuzz()
{  
  if (( $1 % 15 == 0 )); then
    echo 'FizzBuzz'; 
  elif (( $1 % 3 == 0 )); then
    echo 'Fizz'
  elif (( $1 % 5 == 0)); then
    echo 'Buzz'
  else
    echo $1
  fi
}
